import React from 'react';
import ReactDOM from 'react-dom';
import * as serviceWorker from './serviceWorker';
import Root from './Root';
import Markdown from "./Markdown";


const text = `$
\\usepackage[utf8]{inputenc} 3(a - 1) $, якщо $a = 0,7$<br />Перший спосіб:<br />Підставимо дане значення a у вираз та обчислимо його значення:<br />$ 3(a - 1) = 3(0,7 - 1) = 3(-0,3) = -0,9 $<br />Отже, відповідь $-0,9 $, що відповідає варіанту А.<br />Другий спосіб:<br />Розкриємо дужки, перемноживши $3 $ на кожен з доданків та додавши отримані добутки:<br />$3(a - 1) = 3a + 3(-1) = 3a - 3 $<br />Підставимо дане значення $a $ у вираз та обчислимо його значення:<br />$3a - 3 = 3 \\cdot 0,7 - 3 = 2,1 - 3 = -0,9 $<br />Отже, відповідь $-0,9 $, що відповідає варіанту А.`;

ReactDOM.render(
        <div>
            <Markdown source={text}/>
            <Root/>
        </div>,
  document.getElementById('root')
);

serviceWorker.unregister();
