import React from 'react';
import firebase from "../../global"
import {Redirect, Link} from 'react-router-dom';
import axios from "axios";
import ListItem from "../../UI/ListItem";
import QuestionNavPanel from "../../UI/QuestionNavPanel";
import ABCDE from "../Templates/ABCDE/ABCDE";
import Services from "../../Services";
import TestWrapper from "../TestWrapper";

export class TestView extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            testId: this.props.match.params.id,
            user: 25,
            active: 1,
            data: [],
            answered: []
        }
    }



    componentDidMount() {}

    updateQuestion = (x) => {
        this.setState({
            active: x
        });
    }

    updateStatus = (id, x) => {
        const answered = this.state.answered;
        answered[id - 1] = x;
        this.setState({
            answered: answered,
            data
        })
    }

    render() {
        if (this.state.user == 25) {
            return (<div></div>);
        }
        if (this.state.user) {
            if (this.state.data.length > 0) {
                const data = this.state.data;
                let num = this.state.active - 1;
                if (data[num].getType() == "АБВГД") {
                    return (
                        <TestWrapper>
                            <div>
                                <ABCDE
                                    callback={this.updateQuestion}
                                    active={this.state.active}
                                    number={this.state.data.length}
                                    answered={this.state.answered[num]}
                                    data={data}
                                    changeStatus={this.updateStatus}
                                />
                                {document.getElementById("root").click()}
                                {document.getElementById("root").click()}
                            </div>
                        </TestWrapper>
                    )
                }
            }
            return (<div></div>);
        }
        return(<Redirect to="/login"/>);
    }
}

export default TestView;
